import React, { Component } from 'react';
import './sass/main.scss';
import Sourcepage from './Sourcepage';
import NavMenu from './NavMenu';
import NavToggle from './NavToggle';
import topNavBar from '../src/img/Group@2x.png';

class App extends Component {

  constructor() {
    super();
    this.state = {
      menu: false,
      route: 'Abovefold'
    }
  }

  onRouteChange = (route) => {
    this.setState({route: route});
    this.menuToggleHandler();
  }

  ctaRouteChange = (route) => {
    this.setState({route: route});
  }

//   menuToggleHandler = () => {
//     this.setState(({ menu }) => ({
//       menu: !menu
//   }));
// }

  render() {
    return (
      <body className="App">
      {this.state.menu === true ? 
        <NavMenu onRouteChange={this.onRouteChange} /> : 
      <Sourcepage route={this.state.route} ctaRouteChange={this.ctaRouteChange}/>
      }
      <NavToggle click={this.menuToggleHandler}/>
  </body>
    );
  }
}

export default App;
