import React, { Component } from 'react'

export default class FormCollect extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ctrSub: '',
      submitted: false
    }
  }

  onEmailChange = (event) => {
    this.setState({ctrSub: event.target.value})
  }

  onSubmitEmail = () => {
    fetch('http://localhost:3000/register', {
      method: 'post',
      headers: {'content-type': 'application/json'},
      body: JSON.stringify({
        email: this.state.ctrSub
      })
    })
    .then(response => response.json())
    .then(data => {
      if (data === "success") {
        this.setState({submitted: true})
      } 
    })
    .catch(error => console.log(error))
  }


  render() {
    return (
        <div className="ctr">
        {this.state.submitted ? <div> <p className="ctr__confirm">Thanks for subscribing! We will let you know if we get enough interest to produce the product.</p> <button className="purchase" >Back</button> </div>  : 
        <div>
        <div className="ctr__email">
        <form action="#" className="form">
                  <div className="form__group">
                    <input type="email" onKeyPress={this.onKeyPressHandler} onChange={this.onEmailChange} className="form__input" placeholder="Email address" id="email" required />
                    <label htmlFor="email" className="form__label">Enter email address</label>
                  </div>
        </form>
        </div>
        <button className="purchase" onClick={this.onSubmitEmail}>Submit</button> 
        </div>}
      </div>

    )
  }
}
