import React, { Component } from 'react';
import fleeceImg from '../src/img/fleece-img-1.JPG';
import fleeceImg2 from '../src/img/fleece-img-2.JPG';

export default class ProductCarousel extends Component {

  constructor() {
    super();
    this.state = {
      images: [ fleeceImg, fleeceImg2, fleeceImg, fleeceImg2, fleeceImg],
      count: 0
    }
  }

  imageNextHandler = () => {
    this.setState({imageFocused: this.state.images[1]})
  }

  render() {
    return (
      <div className="productGallery">
      <div className="productGallery__others">
        <img src={fleeceImg} alt="image of a fleece" />
        <img src={fleeceImg2} alt="image of a fleece" />
        <img src={fleeceImg} alt="image of a fleece" />
        <img src={fleeceImg} alt="image of a fleece" />
        <img src={fleeceImg} alt="image of a fleece" />
      </div>
      <div className="productGallery__focused">
        <div className="arrows">
            <button className="arrows_right">&larr; Prev</button>
            <button onClick={this.imageNextHandler} className="arrows_left">Next &rarr;</button>
        </div>
        <img src={this.state.imageFocused} alt="image of a fleece" />
      </div>
      </div>
    )
  }
}
