import React, { Component } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import rocketFleece from '../src/img/The rocket fleece@2x.png';
import pullover from '../src/img/pullover.png';
import fleeceImg from '../src/img/fleece-img-1.JPG';
import fleeceImg2 from '../src/img/fleece-img-2.JPG';



export default class Fleece extends Component {

  constructor() {
    super();
    this.state = {
      bought: false
    }
  }

  bought = () => {
    this.setState(({ bought }) => ({
      bought: !bought
  }));
  }

  render() {
    return (
      <div className="rocketFleece">
        <img src={rocketFleece} className="rocketFleece__head" alt="header label"/>
        <img src={pullover} className="rocketFleece__icon" alt="header icon"/>
        <div className="productPage">
        <h1>Rocket Apparel Fleece</h1>
        {/* 

        */}
        {this.state.bought ? <form action="#" className="form">
        <div className="form__group">
          <input type="email" class="form__input" placeholder="Email" id="email" required>
          </input>
          <label for="email" class="form__label">Email</label>
        </div>
      </form>  : <div className="beforeBought">
        <Carousel>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src={fleeceImg}
              alt="First slide"
            />
            <Carousel.Caption>
              <h3>Comfortable fleece</h3>
              <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src={fleeceImg2}
              alt="second slide"
            />

            <Carousel.Caption>
              <h3>Rocket apparel</h3>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src={fleeceImg}
              alt="Third slide"
            />
            <Carousel.Caption>
              <h3>Keeps you warm</h3>
              <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
        <p className="price">&#36;45.99</p>
        <div className="purchase" onClick={this.bought}>Buy now</div>
        </div> }

        </div>
      </div>
    )
  }
}
