import React, { Component } from 'react';
import './sass/main.scss';
import rocketLogo from '../src/img/rocket-02.png';

class App extends Component {

  constructor() {
    super();
    this.state = {
      route: 'home',
      count: 1
    }
  }

  countAdd = () => {
    this.state.count += 1;
  }

  menuToggleHandler = () => {
    if ((this.state.count % 2 ) > 0) {
      console.log(this.state.count + 'reached the home to nav')
      return this.setState({route: 'navigation'});
    } 
    console.log(this.state.count)
    if (this.state.route === 'navigation' && (this.state.count % 2 ) === 0) {
      console.log(this.state.count + 'reached the nav to home')
      this.setState({route: 'navigation'});
      this.countAdd();
    } 
    // console.log(a % 2);
}

  render() {
    return (
      <body className="App">
      {this.state.route === 'navigation' ? 
      <div className="navigation">
        <div className="navigation__nav">
        <ul className="navigation__list">
              <li className="navigation__item"><a className="navigation__link" href="#">Our Story</a></li>
              <li className="navigation__item"><a className="navigation__link" href="#">The Fleece</a></li>
              <li className="navigation__item"><a className="navigation__link" href="#">Contact us</a></li>
              <li className="navigation__item"><a className="navigation__link" href="#">Send feedback</a></li>
        </ul>
        </div>
  </div> : <div className="aboveFold">
<h1>Prepare for the launch of the Rocket</h1>
<div className="logo">
  <img className="rocketLogo" src={rocketLogo} alt="rocket logo"/>
</div>
</div>
    }

      <div className="options" onClick={this.menuToggleHandler}>
          <div className="menu cross menu--1">
    <label>
      <input type="checkbox" />
      <svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
        <circle cx="50" cy="50" r="30" />
        <path className="line--1" d="M0 40h62c13 0 6 28-4 18L35 35" />
        <path className="line--2" d="M0 50h70" />
        <path className="line--3" d="M0 60h62c13 0 6-28-4-18L35 65" />
      </svg>
    </label>
  </div>
  </div>



  </body>
    );
  }
}

export default App;
